mapper.select:
/* sys.CodeCache.getCommonCode */
SELECT
	COMPCD
	, CODEKEY
	, CODE
	, CODE + '[' + SNAME1 + ']' AS VALUE0
	, SNAME1 AS VALUE
	, SNAME2 AS VALUE2
	, SNAME3 AS VALUE3
	, SNAME4 AS VALUE4
	, SNAME5 AS VALUE5
FROM S002 WITH (NOLOCK)
WHERE COMPCD = #{COMPCD}
AND CODEKEY = #{CODEKEY}
AND STATUS = #{STATUS}

AND CODE = #{CODE}


AND SNAME1 = #{SNAME1}


AND SNAME2 = #{SNAME2}


AND SNAME3 = #{SNAME3}


AND SNAME4 = #{SNAME4}


AND SNAME5 = #{SNAME5}

ORDER BY SORTNO, CODE

/* sys.CodeCache.getCommonCodeOnlyName */
SELECT
	COMPCD
	, CODEKEY
	, CODE
	, SNAME1 AS VALUE
	, SNAME2 AS VALUE2
	, SNAME3 AS VALUE3
	, SNAME4 AS VALUE4
	, SNAME5 AS VALUE5
FROM S002 WITH (NOLOCK)
WHERE COMPCD = #{COMPCD}
AND CODEKEY = #{CODEKEY}
AND STATUS = #{STATUS}

AND CODE = #{CODE}


AND SNAME1 = #{SNAME1}


AND SNAME2 = #{SNAME2}


AND SNAME3 = #{SNAME3}


AND SNAME4 = #{SNAME4}


AND SNAME5 = #{SNAME5}

ORDER BY SORTNO, CODE

/* sys.CodeCache.getSearch */ SELECT COMPCD , CODEKEY , NAME , EDITABLE , TABLENAME , REMARK , ADDUSERCD , CONVERT(VARCHAR, ADDDATETIME, 20) AS ADDDATETIME , UPDUSERCD , CONVERT(VARCHAR, UPDDATETIME, 20) AS UPDDATETIME , TERMINALCD FROM S001 WITH (NOLOCK) WHERE COMPCD = #{S_COMPCD}
AND (CODEKEY LIKE UPPER(dbo.UFX_Trim(#{S_CODEKEY})) + '%' OR NAME LIKE '%' + dbo.UFX_Trim(#{S_CODEKEY}) + '%')
AND EDITABLE = #{S_EDITABLE}

/* sys.CodeCache.getCodeSname */
SELECT
	SNAME1
FROM S002 WITH (NOLOCK)
WHERE COMPCD = #{COMPCD}
AND CODEKEY = #{CODEKEY}
AND CODE = #{CODE}

mapper.insert:
/* sys.CodeCache.insertMst */
INSERT INTO S001 WITH (ROWLOCK) (
	COMPCD
	, CODEKEY
	, NAME
	, SNAME
	, EDITABLE
	, TABLENAME
	, REMARK
	, ADDUSERCD
	, ADDDATETIME
	, UPDUSERCD
	, UPDDATETIME
	, TERMINALCD
) VALUES (
	#{COMPCD}
	, #{CODEKEY}
	, #{NAME}
	, #{NAME}
	, #{EDITABLE}
	, #{TABLENAME}
	, #{REMARK}
	, #{LOGIN_USERCD}
	, GETDATE()
	, #{LOGIN_USERCD}
	, GETDATE()
	, #{LOGIN_IP}
)

mapper.update:
/* sys.CodeCache.updateMst */
UPDATE S001 WITH (ROWLOCK)
SET
	NAME = #{NAME}
	, SNAME = #{NAME}
	, EDITABLE = #{EDITABLE}
	, TABLENAME = #{TABLENAME}
	, REMARK = #{REMARK}
	, UPDUSERCD = #{LOGIN_USERCD}
	, UPDDATETIME = GETDATE()
	, TERMINALCD = #{LOGIN_IP}
WHERE COMPCD = #{COMPCD}
AND CODEKEY = #{CODEKEY}

mapper.delete:
/* sys.CodeCache.deleteMst */
DELETE FROM S001 WITH (ROWLOCK)
WHERE COMPCD = #{COMPCD}
AND CODEKEY = #{CODEKEY}

mapper.insert:
/* sys.CodeCache.insertDtl */
INSERT INTO S002 WITH (ROWLOCK) (
	COMPCD
	, CODEKEY
	, CODE
	, SNAME1
