	, SNAME4
	, SNAME5
	, SORTNO
	, REMARK
	, ADDUSERCD
	, ADDDATETIME
	, UPDUSERCD
	, UPDDATETIME
	, TERMINALCD
	, STATUS
) VALUES (
	#{COMPCD}
	, #{CODEKEY}
	, #{CODE}
	, #{SNAME1}
	, #{SNAME2}
	, #{SNAME3}
	, #{SNAME4}
	, #{SNAME5}
	, #{SORTNO}
	, #{REMARK}
	, #{LOGIN_USERCD}
	, GETDATE()
	, #{LOGIN_USERCD}
	, GETDATE()
	, #{LOGIN_IP}
	, #{STATUS}
)

