mapper.select:
/* P000002.getSearch */ SELECT A.COMPCD /* 회사코드 */ , B.NAME AS COMPNM /* 회사명 */ , A.ORGCD /* 셀러코드*/ , A.NAME /* 셀러명 */ , A.SNAME /* 셀러명(약칭) */ , A.COMPANY_EN /* 셀러명(영문) */ , A.TEL1 /* 전화번호1 */ , A.TEL2 /* 전화번호2 */ , A.FAX1 /* 팩스번호1 */ , A.FAX2 /* 팩스번호2 */ , A.POST /* 우편번호 */ , A.ADDR /* 주소 */ , A.ADDR2 /* 주소상세 */ , A.CEO /* 대표자 */ , A.ENG_ADDR /* 영문주소 */ , A.ENG_CEO /* 영문대표자명 */ , A.BIZDATE /* 창립일 */ , A.BIZNO1 /* 사업자번호1 */ , A.BIZNO2 /* 사업자번호2 */ , A.BIZKIND /* 업종 */ , A.BIZTYPE /* 업태 */ , A.EMAIL /* 대표이메일 */ , A.WEBADDR /* 홈페이지 */ , A.NATION /* 국가 */ , A.REMARKS /* 비고 */ , A.ISUSING /* 사용여부 */ , A.ADDUSERCD /* 등록자 */ , CONVERT(VARCHAR(50), A.ADDDATETIME, 20) ADDDATETIME /* 등록일시 */ , A.UPDUSERCD /* 수정자ID */ , CONVERT(VARCHAR(50), A.UPDDATETIME, 20) UPDDATETIME /* 수정일시 */ , A.TERMINALCD /* IP */ , A.PROD_MAP_YN /* 코드대입후 송장발행 */ , A.FILENAME /* 업로드파일명 */ , A.ORIGINFILENM /* 실제저장파일명 */ , A.MEM_USERCD , A.PAYMENT_TYPE /* 결제타입구분 */ , A.PROMOTION_CODE , A.RTN_BANK_NAME /* 환불은행명 */ , A.RTN_BANK_ACCOUNT /* 환불계좌번호 */ , A.RTN_BANK_ACCOUNT_NAME /* 환불은행 예금주 */ FROM P002 A WITH (NOLOCK) JOIN P001 B WITH (NOLOCK) ON A.COMPCD = B.COMPCD WHERE A.COMPCD = #{S_COMPCD}
AND (A.ORGCD LIKE '%' + dbo.UFX_TRIM(#{S_ITEM}) + '%' OR A.NAME LIKE '%' + dbo.UFX_TRIM(#{S_ITEM}) + '%')
AND A.ISUSING = #{S_ISUSING}
ORDER BY A.COMPCD, A.ORGCD ASC

/* p000002.orgcdChk */
SELECT
	COUNT(1) AS CNT 
FROM P002 WITH(NOLOCK)
WHERE COMPCD = #{COMPCD}
AND ORGCD  = #{ORGCD}

mapper.insert:
/* p000002.insertMst */
INSERT INTO P002 WITH (ROWLOCK) (
	COMPCD
	, ORGCD
	, NAME
	, SNAME
	, COMPANY_EN
	, TEL1
	, TEL2
	, FAX1
	, FAX2
	, POST
	, ADDR
	, ADDR2
	, CEO
	, ENG_ADDR
	, ENG_CEO
	, BIZDATE
	, BIZNO1
	, BIZNO2
	, BIZKIND
	, BIZTYPE
	, EMAIL
	, WEBADDR
	, NATION
	, REMARKS
	, ISUSING
	, ADDUSERCD
	, ADDDATETIME
	, UPDUSERCD
	, UPDDATETIME
	, TERMINALCD
	, PROD_MAP_YN
	, PAYMENT_TYPE
) VALUES {
	COMPCD}
	, #{ORGCD}
	, #{NAME}
	, CASE
		WHEN #{SNAME} IS NULL THEN #{NAME}
		WHEN #{SNAME} = '' THEN #{NAME}
		ELSE #{SNAME}
	 END
	, #{COMPANY_EN}
	, #{TEL1}
	, #{TEL2}
	, #{FAX1}
	, #{FAX2}
	, #{POST}
	, #{ADDR}
	, #{ADDR2}
	, #{CEO}
	, #{ENG_ADDR}
	, #{ENG_CEO}
	, #{BIZDATE}
	, #{BIZNO1}
	, #{BIZNO2}
	, #{BIZKIND}
	, #{BIZTYPE}
	, #{EMAIL}
	, #{WEBADDR}
	, #{NATION}
	, #{REMARKS}
	, #{ISUSING}
	, #{LOGIN_USERCD}
	, GETDATE()
	, #{LOGIN_USERCD}
	, GETDATE()
	, #{LOGIN_IP}
	, #{PROD_MAP_YN}
	, #{PAYMENT_TYPE}
)

mapper.update:
/* p000002.updateMst */
UPDATE P002 WITH (ROWLOCK)
SET
	NAME = #{NAME}
	, SNAME = #{SNAME}
	, COMPANY_EN = #{COMPANY_EN}
	, TEL1 = #{TEL1}
	, TEL2 = #{TEL2}
	, FAX1 = #{FAX1}
	, FAX2 = #{FAX2}
	, POST = #{POST}
	, ADDR = #{ADDR}
	, ADDR2 = #{ADDR2}
	, CEO = #{CEO}
	, ENG_ADDR = #{ENG_ADDR}
	, ENG_CEO = #{ENG_CEO}
	, BIZDATE = #{BIZDATE}
	, BIZNO1 = #{BIZNO1}
	, BIZNO2 = #{BIZNO2}
	, BIZKIND = #{BIZKIND}
	, BIZTYPE = #{BIZTYPE}
	, EMAIL = #{EMAIL}
	, WEBADDR = #{WEBADDR}
	, NATION = #{NATION}
	, REMARKS = #{REMARKS}
	, UPDUSERCD = #{LOGIN_USERCD}
	, UPDDATETIME = GETDATE()
	, TERMINALCD = #{LOGIN_IP}
	, PROD_MAP_YN = #{PROD_MAP_YN}
	, PAYMENT_TYPE = #{PAYMENT_TYPE}
	, RTN_BANK_NAME = #{RTN_BANK_NAME}
	, RTN_BANK_ACCOUNT = #{RTN_BANK_ACCOUNT}
	, RTN_BANK_ACCOUNT_NAME = #{RTN_BANK_ACCOUNT_NAME}
WHERE COMPCD = #{COMPCD}
AND ORGCD = #{ORGCD}

mapper.select:
/* p000002.getIsusingChk */
SELECT
	ISUSING 
FROM P002 WITH (NOLOCK) 
WHERE COMPCD = #{COMPCD}
AND ORGCD = #{ORGCD}

mapper.update:
/* p000002.updateMstSts */
UPDATE P002 WITH (ROWLOCK)
SET
	ISUSING = #{ISUSING}
	, UPDUSERCD	= #{LOGIN_USERCD}
	, UPDDATETIME = GETDATE()
	, TERMINALCD = #{LOGIN_IP}
WHERE COMPCD = #{COMPCD}
AND ORGCD = #{ORGCD}
