mapper.select:
/* ShippingListMapper.getSearch */ SELECT o.ORGCD AS orgcd , o.MALL_NM AS mallNm , o.XRT_INVC_SNO AS xrtInvcSno , o.INVC_SNO1 AS invcSno1 , o.INVC_SNO2 AS invcSno2 , o.UPLOAD_DATE AS uploadDate , o.SHIP_METHOD_CD AS shipMethodCd , o.LOCAL_SHIPPER AS localShipper , o.S_NATION AS sNation , o.E_NATION AS eNation , o.RECV_NAME AS recvName , o.RECV_TEL AS recvTel , o.RECV_MOBILE AS recvMobile , o.RECV_ADDR1 AS recvAddr1 , o.RECV_ADDR2 AS recvAddr2 , o.RECV_POST AS recvPost , o.RECV_CURRENCY AS recvCurrency , o.ORD_CNT AS ordCnt , o.ORD_NO AS ordNo , o.CART_NO AS cartNo , CONVERT(CHAR(19), o.STOCK_DATE, 120) AS stockDate , (SELECT TOP 1 STUFF( (SELECT ', ' + od.GOODS_CD FROM TORDERDTL od WHERE 1 = 1 AND o.ORD_CD = od.ORD_CD FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') FROM TORDERDTL ) AS goodsCd , (SELECT TOP 1 STUFF( (SELECT ', ' + od.GOODS_NM + ' ' + od.GOODS_CNT +'ea' FROM TORDERDTL od WHERE 1 = 1 AND o.ORD_CD = od.ORD_CD FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') FROM TORDERDTL ) AS goodsNm , (SELECT TOP 1 STUFF( (SELECT ', ' + od.GOODS_OPTION FROM TORDERDTL od WHERE 1 = 1 AND o.ORD_CD = od.ORD_CD FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') FROM TORDERDTL ) AS goodsOption , (SELECT TOP 1 STUFF( (SELECT ', ' + od.GOODS_CNT FROM TORDERDTL od WHERE 1 = 1 AND o.ORD_CD = od.ORD_CD FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') FROM TORDERDTL ) AS goodsCnt , o.TOT_PAYMENT_PRICE AS totPaymentPrice , CASE WHEN o.STATUS_CD = '10' THEN '주문등록' WHEN o.STATUS_CD = '11' THEN '발송대기' WHEN o.STATUS_CD = '12' THEN '발송완료' WHEN o.STATUS_CD = '20' THEN '입금대기' WHEN o.STATUS_CD = '21' THEN '결제완료' WHEN o.STATUS_CD = '22' THEN '결제대기' WHEN o.STATUS_CD = '23' THEN '결제실패' WHEN o.STATUS_CD = '30' THEN '입고완료' WHEN o.STATUS_CD = '31' THEN '창고보관' WHEN o.STATUS_CD = '32' THEN '출고대기' WHEN o.STATUS_CD = '33' THEN '검수완료' WHEN o.STATUS_CD = '34' THEN '검수취소' WHEN o.STATUS_CD = '35' THEN '선적대기' WHEN o.STATUS_CD = '40' THEN '출고완료' WHEN o.STATUS_CD = '50' THEN '인천공항출발(예정)' WHEN o.STATUS_CD = '51' THEN '인천공항출발(완료)' WHEN o.STATUS_CD = '52' THEN '해외공항도착(예정)' WHEN o.STATUS_CD = '53' THEN '해외공항도착(완료)' WHEN o.STATUS_CD = '54' THEN '수입국통관대기' WHEN o.STATUS_CD = '55' THEN '수입국통관완료' WHEN o.STATUS_CD = '56' THEN '수입국배송시작' WHEN o.STATUS_CD = '57' THEN '수입국배송중' WHEN o.STATUS_CD = '60' THEN '수입국배송완료' WHEN o.STATUS_CD = '80' THEN 'API오류' WHEN o.STATUS_CD = '98' THEN '입금취소' WHEN o.STATUS_CD = '99' THEN '주문취소' ELSE '오류' END AS statusCdKr , CASE WHEN o.STATUS_CD = '10' THEN 'register order' WHEN o.STATUS_CD = '11' THEN 'delivery standby' WHEN o.STATUS_CD = '12' THEN 'delivery completed' WHEN o.STATUS_CD = '20' THEN 'deposit standby' WHEN o.STATUS_CD = '21' THEN 'payment completed' WHEN o.STATUS_CD = '22' THEN 'payment standby' WHEN o.STATUS_CD = '23' THEN 'payment failed' WHEN o.STATUS_CD = '30' THEN 'receiving completed' WHEN o.STATUS_CD = '31' THEN 'storage' WHEN o.STATUS_CD = '32' THEN 'release standby' WHEN o.STATUS_CD = '33' THEN 'inspection complete' WHEN o.STATUS_CD = '34' THEN 'inspection cancel' WHEN o.STATUS_CD = '35' THEN 'waiting for shipment' WHEN o.STATUS_CD = '40' THEN 'release completed' WHEN o.STATUS_CD = '50' THEN 'incheon airport departure (scheduled)' WHEN o.STATUS_CD = '51' THEN 'incheon airport departure (completed)' WHEN o.STATUS_CD = '52' THEN 'overseas airport arrival (scheduled)' WHEN o.STATUS_CD = '53' THEN 'overseas airport arrival (completed)' WHEN o.STATUS_CD = '54' THEN 'import customs clearance standby' WHEN o.STATUS_CD = '55' THEN 'import customs clearance completed' WHEN o.STATUS_CD = '56' THEN 'import country delivery initiated' WHEN o.STATUS_CD = '57' THEN 'import country delivery in progress' WHEN o.STATUS_CD = '60' THEN 'import country delivery completed' WHEN o.STATUS_CD = '80' THEN 'api error' WHEN o.STATUS_CD = '98' THEN 'cancel deposit' WHEN o.STATUS_CD = '99' THEN 'cancel order' ELSE '오류' END AS statusCdEn , o.C_WGT_CHARGE AS cWgtCharge , o.C_WGT_REAL AS cWgtReal , o.C_WGT_VOLUME AS cWgtVolume , o.C_BOX_WIDTH AS cBoxWidth , o.C_BOX_LENGTH AS cBoxLength , o.C_BOX_HEIGHT AS cBoxHeight , o.WGT AS xWgt , o.BOX_WIDTH AS xBoxWidth , o.BOX_LENGTH AS xBoxLength , o.BOX_HEIGHT AS xBoxHeight , CONVERT(CHAR(19), o.ADDDATETIME, 120) AS adddatetime , CONVERT(CHAR(19), o.UPDDATETIME, 120) AS upddatetime , o.SHIP_NAME AS shipName , o.SHIP_TEL AS shipTel , o.SHIP_ADDR AS shipAddr , o.RECV_CITY AS recvCity , o.RECV_STATE AS recvState , o.UNIPASS_TKOFDT AS unipass_tkofdt , CASE WHEN o.SHIPPING_COMPANY is null or o.C_SHIPPING_PRICE = 0 or o.C_SHIPPING_PRICE is null THEN '0' WHEN o.SHIPPING_COMPANY = 'SHIPPO' THEN CONVERT(NVARCHAR(20),CONVERT(NUMERIC(5,2),o.C_SHIPPING_PRICE))+' USD' ELSE CONVERT(NVARCHAR(20),CEILING(o.C_SHIPPING_PRICE)) + ' KRW' END AS cShippingPrice , o.XRT_SHIPPING_PRICE AS xrtShippingPrice , CASE WHEN o.PAYMENT_TYPE = '1' THEN '건당결제' WHEN o.PAYMENT_TYPE = '2' THEN '월말결제' WHEN o.PAYMENT_TYPE = '3' THEN '정기결제' WHEN o.PAYMENT_TYPE = '4' THEN '신용결제' ELSE '-' END AS paymentType , PURCHASE_URL AS purchaseUrl FROM TORDER o WITH (NOLOCK) WHERE 1 = 1 AND o.DEL_FLG = 'N'
AND o.ORGCD = #{sOrgCd}
AND o.WHCD = #{sWhcd}
AND o.E_NATION = #{sFromNation}
AND o.SHIP_METHOD_CD = #{sShipCompany}
AND o.UPLOAD_DATE BETWEEN #{sToDate} AND #{sFromDate}
AND o.STOCK_DATE BETWEEN CONVERT(VARCHAR(10), CONVERT(date, CONVERT(VARCHAR(8), #{sToDate}))) + ' 00:00:00' AND CONVERT(VARCHAR(10), CONVERT(date, CONVERT(VARCHAR(8), #{sFromDate}))) + ' 23:59:59'
AND o.XRT_INVC_SNO = #{sKeyword} OR o.CART_NO = #{sKeyword} OR o.ORD_NO = #{sKeyword} OR o.SELLER_REF_NO1 = #{sKeyword} OR o.SELLER_REF_NO2 = #{sKeyword} OR o.INVC_SNO1 = #{sKeyword} OR o.INVC_SNO2 = #{sKeyword} OR o.SHIP_METHOD_CD = #{sKeyword} OR o.LOCAL_SHIPPER = #{sKeyword} OR o.RECV_NAME LIKE '%' + dbo.UFX_TRIM(#{sKeyword}) + '%' OR o.ORD_CD IN ( SELECT ORD_CD FROM TORDERDTL WHERE 1 = 1
AND ORGCD = #{sOrgCd}
AND GOODS_NM LIKE '%' + dbo.UFX_TRIM(#{sKeyword}) + '%')
AND o.XRT_INVC_SNO = #{sKeyword}
AND o.CART_NO = #{sKeyword}
AND o.ORD_NO = #{sKeyword}
AND o.SELLER_REF_NO1 = #{sKeyword} OR o.SELLER_REF_NO2 = #{sKeyword}
AND o.INVC_SNO1 = #{sKeyword} OR o.INVC_SNO2 = #{sKeyword}
AND o.SHIP_METHOD_CD = #{sKeyword}
AND o.LOCAL_SHIPPER = #{sKeyword}
AND o.RECV_NAME LIKE '%' + dbo.UFX_TRIM(#{sKeyword}) + '%'
AND o.ORD_CD IN (SELECT ORD_CD FROM TORDERDTL WHERE 1 = 1
AND ORGCD = #{sOrgCd}
AND GOODS_NM LIKE '%' + dbo.UFX_TRIM(#{sKeyword}) + '%')
AND o.S_NATION = #{sToNation}
AND o.E_NATION = #{sFromNation}
AND o.PAYMENT_TYPE = #{sPaymentType}
AND STATUS_CD IN ('10', '11', '12')
AND STATUS_CD BETWEEN '20' AND '30'
AND STATUS_CD IN ('31', '32', '33', '34', '35')
AND STATUS_CD IN ('40')
AND STATUS_CD BETWEEN '50' AND '59'
AND STATUS_CD IN ('60')
AND STATUS_CD IN ('32', '80', '99')
AND o.STATUS_CD = #{sStatusCd}
ORDER BY o.XRT_INVC_SNO ASC

/* ShippingListMapper.getTOrder */ SELECT ISNULL(COMPCD , '') AS "compcd" , ISNULL(ORGCD , '') AS "orgcd" , ISNULL(XRT_INVC_SNO , '') AS "xrtInvcSno" , ISNULL(INVC_SNO1 , '') AS "invcSno1" , ISNULL(LOCAL_SHIPPER , '') AS "slug" , ISNULL(STATUS_CD , '') AS "statusCd" , ISNULL(ADDUSERCD , '') AS "addusercd" FROM TORDER WHERE 1 = 1
AND XRT_INVC_SNO = #{xrtInvcSno}
ORDER BY ADDUSERCD ASC

/* ShippingListMapper.getOrder */ SELECT TOP(1) ISNULL(o.ORD_CD , '') AS "ordCd" , ISNULL(o.COMPCD , '') AS "compcd" , ISNULL(o.ORGCD , '') AS "orgcd" , ISNULL(o.ORD_NO , '') AS "ordNo" , ISNULL(o.LOCAL_SHIPPER , '') AS "localShipper" , ISNULL(o.XRT_INVC_SNO , '') AS "xrtInvcSno" , ISNULL(o.INVC_SNO1 , '') AS "invcSno1" , ISNULL(o.LOCAL_SHIPPER , '') AS "slug" , ISNULL(o.RECV_NATION , '') AS "recvNation" , ( CASE WHEN o.ORGCD = 'M00494' THEN '2148-21, Baekjemunhwa-ro, Gongju-si, Chungcheongnam-do' ELSE o.SHIP_ADDR END) AS "shipAddr" , (CASE WHEN o.RECV_NATION = 'US' THEN ISNULL(o.RECV_ADDR1 + ' ' + REPLACE(o.RECV_ADDR2, '.', '') + o.RECV_CITY + ', ' + o.RECV_STATE + ' ' + o.RECV_POST + ', ' + o.RECV_NATION, '') WHEN o.RECV_NATION = 'JP' THEN ISNULL(o.RECV_STATE, '') + ISNULL(o.RECV_CITY, '') + o.RECV_ADDR1 + REPLACE(o.RECV_ADDR2, '-', '') WHEN o.RECV_NATION = 'JP' THEN ISNULL(o.RECV_STATE, '') + ISNULL(o.RECV_CITY, '') + o.RECV_ADDR1 + REPLACE(o.RECV_ADDR2, '.', '') ELSE ISNULL(o.RECV_STATE, '') + ISNULL(o.RECV_CITY, '') + o.RECV_ADDR1 + REPLACE(o.RECV_ADDR2, '.', '') END) AS "recvAddr" , ISNULL(o.STATUS_CD , '') AS "statusCd" , ISNULL(o.ADDUSERCD , '') AS "addusercd" , ISNULL(CONVERT(CHAR(19), o.ADDDATETIME, 120) , '') AS "adddatetime" , (SELECT GOODS_CD FROM TORDERDTL od WHERE 1 = 1 AND od.ORD_CD = o.ORD_CD AND od.ORD_NO = o.ORD_NO AND od.ORD_SEQ = 1 ) AS goodsCd , CASE WHEN o.ORGCD = 'M00494' THEN (SELECT GOODS_EN_NM FROM TORDERDTL od WHERE 1 = 1 AND od.ORD_CD = o.ORD_CD AND od.ORD_NO = o.ORD_NO AND od.ORD_SEQ = 1 ) + '외 ' + CONVERT(NVARCHAR, (CONVERT(INT, ORD_CNT) - 1)) + '건' ELSE (SELECT GOODS_NM FROM TORDERDTL od WHERE 1 = 1 AND od.ORD_CD = o.ORD_CD AND od.ORD_NO = o.ORD_NO AND od.ORD_SEQ = 1 ) + '외 ' + CONVERT(NVARCHAR, (CONVERT(INT, ORD_CNT) - 1)) + '건' END AS goodsNm , ORD_CNT AS goodsCnt , TOT_PAYMENT_PRICE AS paymentPrice FROM TORDER o WHERE 1 = 1
AND o.XRT_INVC_SNO = #{invcSno} OR o.INVC_SNO1 = #{invcSno}
ORDER BY o.ADDUSERCD ASC

/* ShippingListMapper.getTrackingHistory */ SELECT xrtInvcSno,
CASE WHEN CONVERT(INT, statusCd) <= 51 THEN sNation WHEN CONVERT(INT, statusCd) > 51 AND CONVERT(INT, statusCd) <= 60 THEN eNation WHEN CONVERT(INT, statusCd) > 60 AND CONVERT(INT, statusCd) <= 95 THEN sNation WHEN CONVERT(INT, statusCd) = 96 THEN eNation WHEN CONVERT(INT, statusCd) > 96 AND CONVERT(INT, statusCd) <= 99 THEN sNation ELSE sNation END AS eNation,
statusCd, statusNm, statusEnNm, adddatetime FROM( SELECT t.TRACKING_SEQ AS trackingSeq, t.XRT_INVC_SNO AS xrtInvcSno, ( SELECT S_NATION FROM TORDER o WHERE 1 = 1 AND t.XRT_INVC_SNO = o.XRT_INVC_SNO ) AS sNation, t.E_NATION AS eNation, t.STATUS_CD AS statusCd, t.STATUS_NM AS statusNm, t.STATUS_EN_NM AS statusEnNm, CONVERT(VARCHAR, t.ADDDATETIME, 120) AS adddatetime FROM TRACKING_HISTORY t WHERE 1 = 1 ) a WHERE 1 = 1
AND a.xrtInvcSno = #{xrtInvcSno}
ORDER BY a.trackingSeq ASC

mapper.update:
/* ShippingListMapper.updateTorder */ UPDATE TORDER SET
STATUS_CD = #{statusCd},
UPDUSERCD = #{usercd}, UPDDATETIME = getdate() WHERE 1 = 1 AND XRT_INVC_SNO = #{xrtInvcSno}
