mapper.select:
SELECT UPS_COUNTRY_ZONE_SEQ AS upsCountryZoneSeq , COUNTRY_NAME AS countryName , COUNTRY_CODE AS countryCode , ZONE AS zone , ESS AS ess , ADDUSERCD AS addusercd , CONVERT(CHAR(19), ADDDATETIME, 120) AS adddatetime , UPDUSERCD AS updusercd , CONVERT(CHAR(19), UPDDATETIME, 120) AS upddatetime FROM UPS_COUNTRY_ZONE WHERE 1 = 1
AND COUNTRY_NAME LIKE '%' + dbo.UFX_TRIM(#{sKeyword}) + '%'
AND COUNTRY_CODE = #{sCountryCode}
AND ZONE = #{sZone}
ORDER BY UPS_COUNTRY_ZONE_SEQ ASC

mapper.insert:

    SELECT RIGHT('00000000000000000000' + CONVERT(NVARCHAR, NEXT VALUE FOR UPS_COUNTRY_ZONE_SEQ), 20) AS upsCountryZoneSeq

INSERT INTO UPS_COUNTRY_ZONE(
    UPS_COUNTRY_ZONE_SEQ
    , COUNTRY_NAME
    , COUNTRY_CODE
    , ZONE
    , ESS
    , ADDUSERCD
    , ADDDATETIME
    , UPDUSERCD
    , UPDDATETIME
    , TERMINALCD
) VALUES(
    #{upsCountryZoneSeq}
    , #{countryName}
    , #{countryCode}
    , #{zone}
    , #{ess}
    , #{addusercd}
    , getDate()
    , #{updusercd}
    , getDate()
    , #{terminalcd}
)

mapper.update:
UPDATE UPS_COUNTRY_ZONE
SET
    COUNTRY_NAME = #{countryName}
    , COUNTRY_CODE = #{countryCode}
    , ZONE = #{zone}
    , ESS = #{ess}
    , UPDUSERCD = #{updusercd}
    , UPDDATETIME = getDate()
    , TERMINALCD = #{terminalcd}
 WHERE 1 = 1
 AND UPS_COUNTRY_ZONE_SEQ = #{upsCountryZoneSeq}

mapper.delete:
DELETE FROM UPS_COUNTRY_ZONE
WHERE COUNTRY_NAME = #{countryName}
AND COUNTRY_CODE = #{countryCode}
